mapper.select:
select count(*)
from user
where username = #{username} and email = #{email}

mapper.update:
update user
set password = #{password}
where username= #{username}

update user
set avatar = #{avatar}
where username= #{username}

update user
set username = #{newName}
where username= #{oldName}

update user
set email = #{email}
where username= #{username}

update user
set location_id = #{locationId}
where username= #{username}
